targets.expr:
sum without(application, component, pod, service, instance, namespace, job, instance_name, ref, container, group, endpoint) (
    topk(1, 
        chia_blockchain_height !=0 or 
        chia_full_node_node_height !=0
    )
)

sum without(application, component, pod, service, instance, namespace, job, instance_name, ref, container, group, endpoint) (
  topk(1, chia_full_node_block_cost{network="mainnet"}) / 5000000
) * 
sum without(application, component, pod, service, instance, namespace, job, instance_name, ref, container, group, endpoint) (
  bottomk(1,  
    rate(chia_full_node_node_height{network="mainnet"}[5m]) and on (instance) (chia_full_node_node_synced{network="mainnet"} == 1)
  )
) / 3

sum without(application, component, pod, service, instance, namespace, job, instance_name, ref, container, group, endpoint) (
  topk(1, chia_full_node_block_fees{network="mainnet"})
) / 1000000000000

sum without(application, component, pod, service, instance, namespace, job, instance_name, ref, container, group, endpoint) (
  topk(1, 
    chia_blockchain_height or
    chia_full_node_node_height !=0
  )
)

((
  sum without(application, component, pod, service, instance, namespace, job, instance_name, ref, container, group, endpoint) (
    topk(1, 
      chia_blockchain_height or
      chia_full_node_node_height
    ) != 0
  ) - 
  sum without(application, component, pod, service, instance, namespace, job, instance_name, ref, container, group, endpoint) (
    topk(1, 
      chia_blockchain_height offset 3600s or
      chia_full_node_node_height offset 3600s
    ) != 0
  )
)/(3600/18.75))
